//creo el estado inicial
const initialState = {
   recipes : [],
   allRecipes : []
}

//1
function rootReducer (state = initialState, action){
  switch(action.type) {
    case "GET_RECIPES":
        return{
                ...state,
                recipes: action.payload,
                allRecipes : action.payload
          } //hago el primer filtro que es el ascendente y descendente en este caso el score
    case "ORDER_BY_SCORE":
            let orderArray = action.payload==="asc" ? 
            state.recipes.sort(function (a,b){
                    if(a.score > b.score){
                            return 1;
                    }
                    if(b.score > a.score){
                            return -1;
                    }
                    return 0;
            }) :
            state.recipes.sort(function (a,b){
                    if(a.score > b.score){
                            return -1;
                    }
                    if(b.score > a.score){
                            return 1;
                    }
                    return 0;
            });
            return{
                    ...state,
                    recipes:orderArray
            }
            //hacemos la logica del segundo filtro que es la que ordena a las recetas por orden alfabetico
            case 'ORDER_BY_ALPHA':
              let alphaArr = action.payload === 'A-Z' ? state.allRecipes.sort(function (a, b) {
                        if (a.name.toLowerCase() > b.name.toLowerCase()) {
                                      return 1;
                        };
                        if (a.name.toLowerCase() < b.name.toLowerCase()) {
                              return -1;
                        };
                        return 0;
              }) : state.allRecipes.sort(function (a, b) {
                      if (a.name.toLowerCase() > b.name.toLowerCase()) {
                              return -1;
                      };
                      if (a.name.toLowerCase() < b.name.toLowerCase()) {
                              return 1;
                      };
                      return 0;
              });
              return {
                      ...state,
                      recipes: alphaArr
              }
            //este es el ultimo
          default:
            return state;
  }
}

export default rootReducer;
