//terminada la accion y el reducer, hago las importaciones correspondientes
import React, {useEffect} from "react";
import { Link } from 'react-router-dom';
import { getDetail } from "../actions/index";
import { useDispatch, useSelector } from "react-redux";

//y comienzo con la funcion

export default function Detail(props){
    const dispatch = useDispatch();
    useEffect(() =>{
            dispatch(getDetail(props.match.params.id))
    },[dispatch])
//de esta forma yo accedo al id del que estoy buscando
    const recipe = useSelector((state) => state.detail)
 //ahora si, me tengo que traer la receta a mi estado

//renderizo todo
return ( 
    <div>
        {
            recipe.length>0 ? //osea tiene algo?
            <div>
                <h1>Name: {recipe[0].name}</h1>
                <img src= {recipe[0].image} alt="" width="200px" height="200px"/>
                <span>
                <p>Score: {recipe[0].score}</p>
                <p>Paso a Paso: {!recipe[0].createdInDb ? recipe[0].stepByStep?.map((step) => step) : recipe[0].stepbystep}</p>
                </span>
                </div> : <p>Loading..</p>
        } 
        <Link to= "/home">
            <button>Go Back!</button>
        </Link>

    </div>
)


}
