                         <h1>{recipe[0].name}</h1>
                         <img src={recipe[0].image} alt="" width="200px" height="200px"/>
                         <span>
                         <p>Resume: {recipe[0].resume.replace(/<[^>]*>?/g, '')}</p>
                         <p>Type of Diet: {!recipe[0].createdInDb ? recipe[0].diets?.map((diet) => diet) : recipe[0].DietTypes.map((diet) => diet.name)}</p>
                         <p>Score: {recipe[0].score}</p>
                         <p>Healthy Level: {recipe[0].healthylevel}</p>
                         <p>Step by Step {!recipe[0].createdInDb ? recipe[0].stepByStep?.map((step) => step) : recipe[0].stepbystep}</p>
                         </span>
                         <Link to = '/home'>
                                 <button>Go back Home!</button>
                         </Link>
                 </div> : <p>Loading....</p>
}
    </div>
)
}
